insert  into `product`(`id`,`code`,`s_desc`,`description`,`image2`,`image`,`special`,`name`,`alias`,`price`,`created_at`,`updated_at`,`status`,`order`,`authId`,`authName`,`fk_typeid`,`fk_catid`,`index_order`,`thongsosanpham`,`chitietsanpham`,`baohanh`,`vanchuyen`,`nhasanxuat`,`tinhtrang`,`meta_title`,`meta_description`,`meta_keywords`,`fk_catname`,`view`,`promotion`,`tags`) values (2349,'',NULL,'Thiết kế hiện đại, sang trọng Có 3 màu: Ghi nhạt, ghi đậm và đỏ Gồm 1 bàn và 4 ghế sofa Chân ghế : tự chọn ( chân inox - chân gỗ) Đệm mút : Cứng hoặc mềm - bông mút ( tùy chọn ) Bộ bàn ghế sopha đơn nhỏ gọn dễ... ','','upload/product/145708735-1.jpg-00-10-39-27-04-2017','N','Ghế sopha kem','ghe-sopha-kem',5000000,'2017-04-27 00:10:39','2017-05-01 16:41:23',1,0,13,'Administrator',NULL,418,0,'<p>Ch&acirc;n ghế : tự chọn ( ch&acirc;n inox - ch&acirc;n gỗ)</p>\r\n\r\n<p>Đệm m&uacute;t : Cứng hoặc mềm - b&ocirc;ng m&uacute;t ( t&ugrave;y chọn )</p>\r\n\r\n<p>K&iacute;ch thước sofa văng : 1800 * 800. ( K&iacute;ch thước - m&agrave;u sắc c&oacute; thể thay đổi theo y&ecirc;u cầu )</p>\r\n\r\n<p>Cam kết đảm bảo chất lượng v&agrave; gi&aacute; trị của sản phẩm, nguồn gốc xuất xứ vật liệu r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Đặc biệt : Qu&yacute; kh&aacute;ch c&oacute; thể kiểm tra gi&aacute;m s&aacute;t chất lượng sản phẩm sofa theo từng giai đoạn</p>\r\n\r\n<p>Miễn ph&iacute; vận chuyển trong khu vực H&agrave; Nội - Qu&yacute; kh&aacute;ch ở xa ch&uacute;ng t&ocirc;i sẽ hỗ trợ vận chuyển</p>\r\n','<p>Với sự kết hợp h&agrave;i ho&agrave; về m&agrave;u sắc, mẫu m&atilde; v&agrave; kh&ocirc;ng gian kiến tr&uacute;c của ng&ocirc;i nh&agrave;.</p>\r\n\r\n<p>Những t&ocirc;ng m&agrave;u hiện đại, thiết kế trẻ trung theo từng phong c&aacute;ch.</p>\r\n\r\n<p>C&ocirc;ng ty sản xuất sofa đơn đẹp rẻ tại 356 Bạch Mai sẽ mang đến cho bạn những bộ ghế sofa gi&aacute; rẻ hợp với kh&ocirc;ng gian sống.</p>\r\n\r\n<p>Với nhiều mẫu m&atilde; thiết kế đa dạng theo phong c&aacute;ch kh&aacute;c nhau. Những sản phẩm sofa gi&aacute; rẻ tại đ&acirc;y&nbsp;tự h&agrave;o mang đến cho bạn kh&ocirc;ng gian sống ho&agrave;n hảo nhất.</p>\r\n\r\n<p>Khung gỗ : tự nhi&ecirc;n cao cấp</p>\r\n\r\n<p>Ch&acirc;n ghế : tự chọn ( ch&acirc;n inox - ch&acirc;n gỗ)</p>\r\n\r\n<p>Đệm m&uacute;t : Cứng hoặc mềm - b&ocirc;ng m&uacute;t ( t&ugrave;y chọn )</p>\r\n\r\n<p>K&iacute;ch thước sofa văng : 1800 * 800. ( K&iacute;ch thước - m&agrave;u sắc c&oacute; thể thay đổi theo y&ecirc;u cầu )</p>\r\n\r\n<p>Cam kết đảm bảo chất lượng v&agrave; gi&aacute; trị của sản phẩm, nguồn gốc xuất xứ vật liệu r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Đặc biệt : Qu&yacute; kh&aacute;ch c&oacute; thể kiểm tra gi&aacute;m s&aacute;t chất lượng sản phẩm sofa theo từng giai đoạn</p>\r\n\r\n<p>Miễn ph&iacute; vận chuyển trong khu vực H&agrave; Nội - Qu&yacute; kh&aacute;ch ở xa ch&uacute;ng t&ocirc;i sẽ hỗ trợ vận chuyển</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n','','','',1,'Ghế sopha kem','','',NULL,0,23,'noi-that,tu-bep'),(2350,'',NULL,'Đây là một trong những mẫu sofa dành cho phòng khách nhỏ, hẹp thường dành cho khoảng 2, 3 người ngồi. Sản phẩm có chân inox. Đệm giả rời, và tựa lưng cũng như gối đều đen. Bạn có thể kết hợp mẫu sofa này với những mẫu sofa văng khác hoặc sofa đơn. - Vỏ bọc: nỉ vải cao cấp nhập khẩu từ Indo... ','','upload/product/523708429-2-1.jpg-10-46-10-29-04-2017','N','Sopha đôi da','sopha-doi-da',8000000,'2017-04-27 00:17:51','2017-05-01 16:52:00',1,0,13,'Administrator',NULL,418,0,'','<div class=\"tab-pane active\" id=\"tab_1\">\r\n<p>Đ&acirc;y l&agrave; một trong những mẫu sofa d&agrave;nh cho ph&ograve;ng kh&aacute;ch nhỏ, hẹp thường d&agrave;nh cho khoảng 2, 3 người ngồi. Sản phẩm c&oacute; ch&acirc;n inox.</p>\r\n\r\n<p>Đệm giả rời, v&agrave; tựa lưng cũng như gối đều đen. &nbsp;Bạn c&oacute; thể kết hợp mẫu sofa n&agrave;y với những mẫu sofa văng kh&aacute;c hoặc sofa đơn.&nbsp;</p>\r\n\r\n<p>- Vỏ bọc: nỉ vải cao cấp nhập khẩu từ Indo</p>\r\n\r\n<p>- Khung sườn: gỗ tự nhi&ecirc;n đ&atilde; qua xử l&yacute; chống cong v&ecirc;nh, mối mọt.</p>\r\n\r\n<p>- Ch&acirc;n ghế: gỗ.</p>\r\n\r\n<p>- Đệm m&uacute;t: cứng, mềm, hoặc bằng b&ocirc;ng &eacute;p (t&ugrave;y chọn).</p>\r\n\r\n<p>- L&ograve; xo: l&otilde;i th&eacute;p cứng c&oacute; độ bền cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://media.bizwebmedia.net/sites/96971/data/Upload/2015/1/29_1%281%29.jpg\" style=\"height:487px; width:472px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Với thiết kế sang trọng c&ugrave;ng kiểu d&aacute;ng lịch l&atilde;m, bộ sofa đ&ocirc;i sẽ mang đến cảm gi&aacute;c gần gũi v&agrave; th&acirc;n thiện với kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch nh&agrave; bạn ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\r\n\r\n<p>Li&ecirc;n hệ ngay đến địa chỉ sản xuất sofa đ&ocirc;n để được đặt h&agrave;ng.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i lu&ocirc;n lu&ocirc;n hỗ trợ c&aacute;c Qu&yacute; kh&aacute;ch h&agrave;ng nhiệt t&igrave;nh</p>\r\n\r\n<p>Sofa đ&ocirc;i với thiết kế đơn giản, m&agrave;u sắc nhẹ nh&agrave;ng ph&ugrave; hợp với kh&ocirc;ng gian bị hạn chế th&ecirc;m v&agrave;o đ&oacute; rất dễ d&agrave;ng kết hợp với nội thất xung quanh tạo cho căn ph&ograve;ng của bạn th&ecirc;m ấn tượng.</p>\r\n\r\n<p>Được l&agrave;m từ chất liệu cao cấp, mẫu ghế sofa đ&ocirc;n sẽ l&agrave; sự lựa chọn ho&agrave;n hảo cho kh&ocirc;ng gian của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://media.bizwebmedia.net/sites/96971/data/Upload/2015/1/29_2%281%29.jpg\" style=\"height:487px; width:472px\" /></p>\r\n</div>\r\n','','','',1,'Sopha đôi da','','',NULL,0,0,'noi-that,tu-bep');

